import React, { Fragment, useState, useEffect } from "react";
import Peer from "peerjs";
import socketIOClient from "socket.io-client";
import "./videochatstyles.css";
let client = {};

const Videochat = () => {
  const [clientInfo, setClientInfo] = useState({
    clientID: -1
  });

  var str = null;
  var clientID;
  const socket = socketIOClient("http://127.0.0.1:5000");

  const onsubmit = async e => {
    e.preventDefault();
    var id = Math.floor(Math.random() * 999999);
    console.log(id);
    document.getElementById("clientID").textContent = id;
  };
  return (
    <Fragment>
      {/* <button onClick={() => handleNewMessage()}>Emit new message</button> */}
      <div>
        <form className="form" onSubmit={e => onsubmit(e)}>
          <div className="form-group">
            <input type="text" placeholder="Name" name="name" required />
          </div>
          <div className="form-group">
            <label id="clientID">Client ID: </label>
          </div>

          <input
            type="submit"
            className="btn btn-primary"
            value="Start Video Chat"
            required
          />
        </form>
      </div>
      <div className="container parent-box row h-90 w-100">
        <div class="col-12 col-sm-4 d-flex justify-content-center video-box">
          <div class="embed-responsive embed-responsive-16by9">
            <video class="embed-responsive-item" muted />
          </div>
        </div>
        <div class="col-12 col-sm-4 d-flex justify-content-center video-box">
          <div class="embed-responsive embed-responsive-16by9">
            <video class="embed-responsive-item" muted />
          </div>
        </div>
        {/* <div className="col-12 col-sm-6 d-flex justify-content-center video-box" /> */}
        {/* <div className="col-12 col-sm-6 d-flex justify-content-center video-box" /> */}
      </div>

      <div class="row h-90 w-100">
        <div class="col-12 col-sm-6 d-flex justify-content-center">
          <div class="embed-responsive embed-responsive-16by9">
            <video class="embed-responsive-item" muted />
          </div>
        </div>
        <div class="col-12 col-sm-6 d-flex justify-content-center">
          <div id="peerDiv" class="embed-responsive embed-responsive-16by9" />
        </div>
      </div>
    </Fragment>
  );
};

export default Videochat;
