import React, { Fragment, useState } from "react";
import axios from "axios";
const Register = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    password2: ""
  });
  const { name, email, password, password2 } = formData;
  //we are using hookstate to use default date is know as formData and to change the value of form and store it we use a self defined function setFormData

  //formData is just an object which is a state of the data

  // since its an object in setFormData so we do in curlyBraces inside it
  //... is a spread operator which copies the formData object
  // then we change the value of the formData Object
  //e is the field and hence we pick the target's name attribute such as name. email, password, password2 and set it to the value that the user is inputiing and change the formData
  const onChange = e =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = e => {
    e.preventDefault();
    if (password !== password2) {
      console.log("Passwords don't match!");
    } else {
      console.log("Passwords Match!");
      console.log(formData);
    }
  };
  return (
    <Fragment>
      <h1 className="large text-primary">Sign Up</h1>
      <p className="lead">
        <i className="fas fa-user" /> Create Your Account
      </p>
      <form className="form" onSubmit={e => onSubmit(e)}>
        <div className="form-group">
          <input
            type="text"
            placeholder="Name"
            name="name"
            value={name}
            onChange={e => onChange(e)}
            required
          />
        </div>
        <div className="form-group">
          <input
            type="email"
            placeholder="Email Address"
            name="email"
            value={email}
            onChange={e => onChange(e)}
            required
          />
          <small className="form-text">
            This site uses Gravatar so if you want a profile image, use a
            Gravatar email
          </small>
        </div>
        <div className="form-group">
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={e => onChange(e)}
            name="password"
            minLength="6"
            required
          />
        </div>
        <div className="form-group">
          <input
            type="password"
            placeholder="Confirm Password"
            value={password2}
            onChange={e => onChange(e)}
            name="password2"
            minLength="6"
            required
          />
        </div>
        <input
          type="submit"
          className="btn btn-primary"
          value="Register"
          required
        />
      </form>
      <p className="my-1">
        Already have an account? <a href="login.html">Sign In</a>
      </p>
    </Fragment>
  );
};

export default Register;
