import React, { Fragment, useState, useEffect } from "react";
import Peer from "peerjs";
import socketIOClient from "socket.io-client";

let client = {};

const Videochat = () => {
  const [clientInfo, setClientInfo] = useState(0);
  const socket = socketIOClient("http://127.0.0.1:5000");
  var str = null;

  useEffect(() => {
    socket.emit("NewClient", window.location.host);
  });
  useEffect(() => {
    socket.on("PeerID", id => {
      setClientInfo(id);
    });
  });
  return (
    <Fragment>
      {/* <button onClick={() => handleNewMessage()}>Emit new message</button> */}
      <div>
        Video
        <video />
      </div>
      <div id="peerVid">
        Video2
        <video id="videoP" />
      </div>
    </Fragment>
  );
};

export default Videochat;
