import React, { Fragment, useState, useEffect } from "react";
import socketIOClient from "socket.io-client";
// let Peer = require("simple-peer");
// import * as Peer from "simple-peer";
let Peer = require("simple-peer");

// const video = document.querySelector("video");
let client = {};

// We can also pass an object with width and height for the video parameter of the getUSerMedia
const Videochat = () => {
  const [socketinfo, setsocketinfo] = useState({
    response: "false",
    endpoint: "http://127.0.0.1:5000"
  });
  const socket = socketIOClient(socketinfo.endpoint);
  var str = null;

  useEffect(() => {
    const video = document.querySelector("video");

    //Getting our own video
    navigator.mediaDevices
      .getUserMedia({ video: true, audio: true })
      .then(stream => {
        socket.emit("NewClient");
        video.srcObject = stream;
        video.play();

        //Initializing Peer
        function InitPeer(type) {
          let peer = new Peer({
            initiator: type == "init" ? true : false,
            stream: stream,
            trickle: false
          });
          peer.on("stream", function(stream) {
            createVideo(stream);
          });
          peer.on("close", function() {
            document.getElementById("peerVideo").remove();
            peer.destroy();
          });
          return peer;
        }

        //function makePeer to send the offer
        function makePeer() {
          client.gotAnswer = false;
          let peer = InitPeer("init");
          peer.on("signal", function(data) {
            if (!client.gotAnswer) {
              socket.emit("offer", data);
            }
          });
          client.peer = peer;
        }

        // FrontAnswer to respond to the offer sent my another client
        function frontAnswer(offer) {
          let peer = InitPeer("notInit");
          peer.on("signal", data => {
            socket.emit("Answer", data);
          });
          peer.signal(offer);
        }

        //signal answer- handles answer from the backend
        function signalAnswer(answer) {
          client.gotAnswer = true;
          let peer = client.peer;
          peer.signal(answer);
        }
        //creating a video element

        function createVideo(stream) {
          let video = document.createElement("video");
          video.id = "peerVideo";
          video.srcObject = stream;
          video.class = "embed-responsive-item";
          document.querySelector("#peerDiv").appendChild(video);
          video.play();
        }

        function sessionActive() {
          document.write("Session already in use- Please come back later");
        }
        socket.on("BackOffer", frontAnswer);
        socket.on("BackAnswer", signalAnswer);
        socket.on("SessionActive", sessionActive);
        socket.on("CreatePeer", makePeer);
      })
      .catch(err => document.write(err));

    console.log("Own Video Started");

    //create a socket connection

    //ends the function for useEffect hook

    //
  }, []);
  return (
    <Fragment>
      <div className="container">
        <div className="row h-100 w-100">
          <p>Gekk</p>
          <div className="col-12 col-6 d-flex justify-content-center">
            <div className="embed-responsive embed-responseive-16by9">
              <video className="embed-responsive-item" muted />
              {/* We muted the above video as we dont want to hear our own voice  also the above raios and sizes fix the video to cover the entire screen*/}
            </div>
          </div>

          <div className="col-12 col-6 d-flex justify-content-center">
            <div
              id="peerVideo"
              className="embed-responsive embed-responseive-16by9"
            >
              {/* We muted the above video as we dont want to hear our own voice  also the above raios and sizes fix the video to cover the entire screen*/}
            </div>
          </div>
        </div>
      </div>
    </Fragment>
  );
};

export default Videochat;
